import React, {Component} from 'react';
import axios from 'axios';

class AllNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: []
    }
  }

  fetchNews = async (event) => {
    const api_endpoint = "http://newsapi.org/v2/everything?q=business&sortBy=publishedAt&apiKey=8e9f1bf2b8c841278301c14cf986965e";

    await axios.get(api_endpoint).then(function (response){
      console.log(response);
      this.setState({news: response});
    }).catch(function (err){
      console.log(err.message);
    })
  }

  render() {
    return (
      <div id="portfolio-grid" className="row no-gutter">
        <div className="item web col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="#" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Boxed Water</h3>
              <span>Web</span>
            </div>
            <img className="img-fluid" alt="Hello" src="img/img_1.jpg" />
          </a>
        </div>

      </div>
    );
  }

}

export default AllNews;
