import React, {Component} from 'react';
import axios from 'axios';

class AllNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: []
    }
  }

  componentDidMount = async () => {
    const api_endpoint = "http://newsapi.org/v2/everything?q=business&sortBy=publishedAt&apiKey=8e9f1bf2b8c841278301c14cf986965e";

    const self = this;

    await axios.get(api_endpoint).then(function (response){
      const data = response.data;
      const news = data.articles.slice(0, 20);
      console.log(news);

      self.setState({
        news: news
      });
    }).catch(function (err){
      console.log(err);
    })
  }

  render() {
    return this.state.news.map((item) => (
      <div id={item.url} className="row no-gutter">
        <div key={item.url} className="item web col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href={item.url} className="item-wrap fancybox">
            <div className="work-info">
              <h3>{item.title}</h3>
              <span>{item.source.name}</span>
            </div>
            <img className="img-fluid" alt={item.title} src={item.urlToImage} />
          </a>
        </div>
      </div>
    ));
  }

}

export default AllNews;
