import React, {Component} from 'react';
import axios from 'axios';

class AllNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: []
    }
  }

  fetchNews = async (event) => {
    const api_endpoint = "http://newsapi.org/v2/everything?q=business&sortBy=publishedAt&apiKey=8e9f1bf2b8c841278301c14cf986965e";

    await axios.get(api_endpoint).then(function (response){
      console.log(response);
      this.setState({news: response});
    }).catch(function (err){
      console.log(err.message);
    })
  }

  render() {
    return (
      <div id="portfolio-grid" className="row no-gutter">
        <div className="item web col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="#" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Boxed Water</h3>
              <span>Web</span>
            </div>
            <img className="img-fluid" alt="Hello" src="img/img_1.jpg" />
          </a>
        </div>
        <div className="item photography col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="work-single.html" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Build Indoo</h3>
              <span>Photography</span>
            </div>
            <img className="img-fluid" src="img/img_2.jpg" />
          </a>
        </div>
        <div className="item branding col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="work-single.html" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Cocooil</h3>
              <span>Branding</span>
            </div>
            <img className="img-fluid" src="img/img_3.jpg" />
          </a>
        </div>
        <div className="item design col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="work-single.html" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Nike Shoe</h3>
              <span>Design</span>
            </div>
            <img className="img-fluid" src="img/img_4.jpg" />
          </a>
        </div>
        <div className="item photography col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="work-single.html" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Kitchen Sink</h3>
              <span>Photography</span>
            </div>
            <img className="img-fluid" src="img/img_5.jpg" />
          </a>
        </div>
        <div className="item branding col-sm-6 col-md-4 col-lg-4 mb-4">
          <a href="work-single.html" className="item-wrap fancybox">
            <div className="work-info">
              <h3>Amazon</h3>
              <span>brandingn</span>
            </div>
            <img className="img-fluid" src="img/img_6.jpg" />
          </a>
        </div>
      </div>
    );
  }

}

export default AllNews;
