import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';

import Footer from './elements/Footer.js';
import Header from './elements/Header.js';
import About from './elements/About.js';

import AllNews from './news/AllNews.js';

/*
import BusinessNews from './news/BusinessNews.js';
import PoliticalNews from './news/PoliticalNews.js';
import SportNews from './news/SportNews.js';
import CryptoNews from './news/CryptoNews.js';
*/

import './App.css';

class App extends Component {
  /*
  constructor(props) {
    super(props);
    this.state = {

    }
  }
  */

  render() {
    return (
      <Router>
        <div>
          <div id="main">
            <div className="site-section site-portfolio">
              <div className="container">
                <Header />

                <Switch>
                  <Route path="/" component={AllNews} topic="all" ></Route>
                  <Route path="/politics" component={AllNews} topic="politics"></Route>
                  <Route path="/business" component={AllNews} topic="business"></Route>
                  <Route path="/sports" component={AllNews} topic="sports"></Route>
                  <Route path="/cryptos" component={AllNews} topic="cryptos"></Route>
                </Switch>
              </div>
            </div>

            <About />  
          </div>

          <Footer />
        </div>
      </Router>
    );
  }

}

export default App;
