import React, {Component} from 'react';

class SingleNewsItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image : "../../img/news_placeholder.jpg"
    }
  }

  // check if image exists
  checkImageExists = (image) => {
    fetch(image, { method: 'HEAD' })
    .then(res => {
      if (res.ok) {
        image = res.url;
      }

      this.setState({
        image: image
      });
    }).catch(err => console.log('Error:', err));
  }

  componentDidMount = () => {
    this.checkImageExists(this.props.item.urlToImage);
  }

  componentWillUnmount = () => {

  }

  render() {
    const item = this.props.item;
    return (
      <div className="item web col-sm-6 col-md-4 col-lg-4 mb-4">
        <div className="card">
          <div className="card-img-box">
            <img className="card-img-top" src={this.state.image} alt={item.title} />
          </div>
          <div className="card-body">
            <h6 className="card-title">{item.title}</h6>
            <p className="card-text">{item.source.name}</p>
            <a href={item.url} className="btn btn-danger" title={item.title} target="_blank" rel="noopener noreferrer">Read Full Article</a>
          </div>
        </div>
      </div>
    );
  }

}

export default SingleNewsItem;
