import React from 'react';

const SingleNewsItem = ({item}) => {
  // check if image exists

  checkImageExists = ({item}) => {
    let image = item.urlToImage;

    fetch(image, { method: 'HEAD' })
    .then(res => {
        if (res.ok) {
          // Image exists.  
          image = item.urlToImage;
        } else {
          // Image does not exist.
          image = "../../img/news_placeholder.jpg";
        }

        return image;
    }).catch(err => console.log('Error:', err));  
  }

  componentDidMount = () => {
    image = checkImageExists(item);
  }

  return (
    <div className="item web col-sm-6 col-md-4 col-lg-4 mb-4">
      <div className="card">
        <div className="card-img-box">
          <img className="card-img-top" src={image} alt={item.title} />
        </div>
        <div className="card-body">
          <h6 className="card-title">{item.title}</h6>
          <p className="card-text">{item.source.name}</p>
          <a href={item.url} className="btn btn-danger" title={item.title} target="_blank" rel="noopener noreferrer">Read Full Article</a>
        </div>
      </div>
    </div>
  )
}

export default SingleNewsItem;
