import React, {Component} from 'react';
import axios from 'axios';

import SingleNewsItem from './SingleNewsItem.js';

class AllNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: []
    }
  }

  componentDidMount = async () => {
    const api_endpoint = "http://newsapi.org/v2/everything?q=business&sortBy=publishedAt&apiKey=8e9f1bf2b8c841278301c14cf986965e";

    const self = this;

    await axios.get(api_endpoint).then(function (response){
      const data = response.data;
      const news = data.articles.slice(0, 20);
      console.log(news);

      self.setState({
        news: news
      });
    }).catch(function (err){
      console.log(err);
    })
  }

  render() {
    return this.state.news.map((item) => (
      <div className="row no-gutter">
        <SingleNewsItem item={item}/>
      </div>
    ));
  }

}

export default AllNews;
